TriggerOraclev10g.body:
declare 
   l_table    varchar2(30) := 'MEDSROLE';
   l_action   varchar2(1);
   l_seq      number; 
begin 
   if inserting then 
      if :new.MEDSROLE is null then 
         :new.MEDSROLE := to_number(sys_guid(),'xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx');
      end if; 
      l_seq := :new.MEDSROLE;
      l_action := 'I';
   elsif updating then 
      l_seq := :new.MEDSROLE;
      l_action := 'U';
   elsif deleting then 
      l_seq := :old.MEDSROLE;
   end if; 
   if deleting then
      cm.audit_tool.audit_delete('MEDSROLE',l_seq);
   else
      cm.audit_tool.audit_column(l_action,'MEDSROLE','LABEL',l_seq,:old.LABEL,:new.LABEL);
      cm.audit_tool.audit_column(l_action,'MEDSROLE','LABEL_INTERNAL',l_seq,:old.LABEL_INTERNAL,:new.LABEL_INTERNAL);
   end if;
end b_medsrole;
