TriggerOraclev10g.body:
declare 
   l_table    varchar2(30) := 'MEDSDOMAIN';
   l_action   varchar2(1);
   l_seq      number; 
begin 
   if inserting then 
      if :new.MEDSDOMAIN is null then 
         :new.MEDSDOMAIN := to_number(sys_guid(),'xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx');
      end if; 
      l_seq := :new.MEDSDOMAIN;
      l_action := 'I';
   elsif updating then 
      l_seq := :new.MEDSDOMAIN;
      l_action := 'U';
   elsif deleting then 
      l_seq := :old.MEDSDOMAIN;
   end if; 
   if deleting then
      cm.audit_tool.audit_delete('MEDSDOMAIN',l_seq);
   else
      cm.audit_tool.audit_column(l_action,'MEDSDOMAIN','LABEL',l_seq,:old.LABEL,:new.LABEL);
   end if;
end b_medsdomain;
