TriggerOraclev10g.body:
declare 
   l_table    varchar2(30) := 'MEDSUSER';
   l_action   varchar2(1);
   l_seq      number; 
begin 
   if inserting then 
      if :new.MEDSUSER is null then 
         :new.MEDSUSER := to_number(sys_guid(),'xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx');
      end if; 
      l_seq := :new.MEDSUSER;
      l_action := 'I';
   elsif updating then 
      l_seq := :new.MEDSUSER;
      l_action := 'U';
   elsif deleting then 
      l_seq := :old.MEDSUSER;
   end if; 
   if deleting then
      cm.audit_tool.audit_delete('MEDSUSER',l_seq);
   else
      cm.audit_tool.audit_column(l_action,'MEDSUSER','CMUSER_TENANT',l_seq,:old.CMUSER_TENANT,:new.CMUSER_TENANT);
      cm.audit_tool.audit_column(l_action,'MEDSUSER','FULL_NAME',l_seq,:old.FULL_NAME,:new.FULL_NAME);
   end if;
end b_medsuser;
