TriggerOraclev10g.body:
declare 
   l_table    varchar2(30) := 'MEDSPLAYER';
   l_action   varchar2(1);
   l_seq      number; 
begin 
   if inserting then 
      if :new.MEDSPLAYER is null then 
         :new.MEDSPLAYER := to_number(sys_guid(),'xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx');
      end if; 
      l_seq := :new.MEDSPLAYER;
      l_action := 'I';
   elsif updating then 
      l_seq := :new.MEDSPLAYER;
      l_action := 'U';
   elsif deleting then 
      l_seq := :old.MEDSPLAYER;
   end if; 
   if deleting then
      cm.audit_tool.audit_delete('MEDSPLAYER',l_seq);
   else
      cm.audit_tool.audit_column(l_action,'MEDSPLAYER','MEDSROLE',l_seq,:old.MEDSROLE,:new.MEDSROLE);
      cm.audit_tool.audit_column(l_action,'MEDSPLAYER','MEDSUSER',l_seq,:old.MEDSUSER,:new.MEDSUSER);
   end if;
end b_medsplayer;
