TriggerOraclev10g.body:
declare 
   l_table    varchar2(30) := 'MEDSLAYER';
   l_action   varchar2(1);
   l_seq      number; 
begin 
   if inserting then 
      if :new.MEDSLAYER is null then 
         :new.MEDSLAYER := to_number(sys_guid(),'xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx');
      end if; 
      l_seq := :new.MEDSLAYER;
      l_action := 'I';
   elsif updating then 
      l_seq := :new.MEDSLAYER;
      l_action := 'U';
   elsif deleting then 
      l_seq := :old.MEDSLAYER;
   end if; 
   if deleting then
      cm.audit_tool.audit_delete('MEDSLAYER',l_seq);
   else
      cm.audit_tool.audit_column(l_action,'MEDSLAYER','LABEL',l_seq,:old.LABEL,:new.LABEL);
      cm.audit_tool.audit_column(l_action,'MEDSLAYER','ICON',l_seq,:old.ICON,:new.ICON);
      cm.audit_tool.audit_column(l_action,'MEDSLAYER','COLOR',l_seq,:old.COLOR,:new.COLOR);
   end if;
end b_medslayer;
