TriggerOraclev10g.body:
declare 
   l_table    varchar2(30) := 'MEDSUSER_MEDSDOMAIN';
   l_action   varchar2(1);
   l_seq      number; 
begin 
   if inserting then 
      if :new.MEDSUSER_MEDSDOMAIN is null then 
         :new.MEDSUSER_MEDSDOMAIN := to_number(sys_guid(),'xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx');
      end if; 
      l_seq := :new.MEDSUSER_MEDSDOMAIN;
      l_action := 'I';
   elsif updating then 
      l_seq := :new.MEDSUSER_MEDSDOMAIN;
      l_action := 'U';
   elsif deleting then 
      l_seq := :old.MEDSUSER_MEDSDOMAIN;
   end if; 
   if deleting then
      cm.audit_tool.audit_delete('MEDSUSER_MEDSDOMAIN',l_seq);
   else
      cm.audit_tool.audit_column(l_action,'MEDSUSER_MEDSDOMAIN','MEDSUSER',l_seq,:old.MEDSUSER,:new.MEDSUSER);
      cm.audit_tool.audit_column(l_action,'MEDSUSER_MEDSDOMAIN','MEDSDOMAIN',l_seq,:old.MEDSDOMAIN,:new.MEDSDOMAIN);
   end if;
end b_medsuser_medsdomain;
